module.exports = function(config){
	var hostName = ""
		,	protocol = "http";
	if(config.domainName != null){
		hostName = config.domainName;
	}
	
	
	return {
		host: host
	,	protNhost: protNhost
	,	useHostRedirect: useHostRedirect
	}
	
	
	function host(){
		return hostName;
	}
	
	function protNhost(){
		if(hostName){
			return protocol + "://" + hostName;
		}
		return "";
	}
	
	function useHostRedirect(req, res, next){
		res.hostRedirect = function(arg){
			res.redirect(protNhost() + arg);
		}
		next();
	}
};
