module.exports = (function(){
	var unsigned = /u/;
	
	
	function numeric(arg, opts){
		arg = arg - 0;
		if(isNaN(arg)){
			return null;
		}else{
			if((typeof opts == "string" && unsigned.test(opts))
			|| (typeof opts == "object" && unsigned.test(opts.type))){
				if(arg < 0){
					return null;
				}
			}
			return arg;
		}
	}
	
	function int(arg, opts){
		arg = numeric(arg, opts);
		if(typeof arg == "number" && Math.floor(arg) === arg){
			return arg;
		}else{
			return null;
		}
	}
	
	function sParse(data, defVal){
		try{
			data = JSON.parse(data);
			return data;
		}catch(err){
			return defVal;
		}
	}
	
	return {
		int: int
	,	numeric: numeric
	,	sParse: sParse
	}
})();
